<?phpif ($_REQUEST['num'] && $_REQUEST['num'] > 0){	$n = $_REQUEST['num'];	function GeneratePins($n = 1){		$pins = array();		for($i = 0; $i < $n; $i++){			$pins[] = genPin();		}		return $pins;	}	function genPin(){		$pin = date("z");		while(strlen($pin) < 3) $pin = "0" . $pin;				for($i = 0; $i < 13; $i++){			$pin .= mt_rand(0,9);		}		return $pin;	}		$pins = GeneratePins($n);		$pinRecs = array();	foreach($pins as $pn){		$pinRecs[] = array(	$pn, $_REQUEST['val'] );	}		if (DBHelper::Insert('pins', $pinRecs, array('code', 'value'))){		foreach($pins as $pn) print "$pn <br>";	}}else{?><!DOCTYPE html><html> <head><title>Make Pins</title></head> <body>   <div style="height:300px"></div>   <div style="text-align:center">	<form action="" method="get">		<input name="action" value="<?php print $_REQUEST['action'] ?>" type="hidden">		<label>Cantidad</label><input name="num" type="text"><br>		<label>Valor</label><input name="val" type="text"><br>		<input type="submit" value="Create">	</form>   </div> </body></html><?php}