<?phpif (isset($_POST['q'])){	$query = stripslashes($_POST['q']);	$error = "";	$data = @DBHelper::Query($query, $error);	if (is_array($data)){		print "<table cellspacing=0>";		if (count($data) > 0){			print "<tr>";			foreach($data[0] as $key => $val){				print "<td style='background:gray; padding:4px 6px;'>$key</td>";			}			print "</tr>";		}		foreach($data as $row){			print "<tr>";			foreach($row as $val){				print "<td style='padding:4px 6px'>$val</td>";			}			print "</tr>";		}		print "</table>";	}else{		var_dump($data, $error);	}}else{?><!DOCTYPE html><html>  <head>	<title>Query MYSQL</title>  </head>    <body style="padding: 20px">	<h3>Query MYSQL :</h3>	<form action="" method="post" enctype="multipart/form-data">	  <input type="hidden" value="query_mysql" name="action">	  <textarea name="q" rows="10" cols="60"></textarea><br><br>	  <input type="submit" value="Query">	</form>  </body></html><?php}